rule "Veilid Parse RPC Set Value"
when
    starts_with(to_string($message.message), "IN <=== SetValueQ")
then
    set_field("vendor_event_category", "rpc");
    set_field("vendor_event_action", "set");
    set_fields(grok("IN <=== SetValueQ\\(%{GREEDYDATA:veilid_key} #{GREEDYDATA:veilid_subkey} len=%NUMBER:veilid_data_length} seq=%{DATA:veilid_seq} writer=%{GREEDYDATA:veilid_writer}\\) <== %{GREEDYDATA:veilid_sender_node_id}", to_string($message.message), true));
end

rule "Veilid Parse Get or Create Connection Retry"
when
    starts_with(to_string($message.message), "get_or_create_connection retries left", true)
then
    // https://gitlab.com/veilid/veilid/-/blob/main/veilid-core/src/network_manager/connection_manager.rs#L360
    set_field("vendor_event_category", "connection");
    set_field("vendor_event_action", "retry");
    set_fields(grok("get_or_create_connection retries left: %{NUMBER:retries_left}", to_string($message.message), true));
end

