end

rule "Veilid Parse No Protocol Handler"
when
    starts_with(to_string($message.message), "no protocol handler for connection from")
then
    // https://gitlab.com/veilid/veilid/-/blob/main/veilid-core/src/network_manager/native/network_tcp.rs#L221
    set_field("vendor_event_category", "network");
    set_field("vendor_event_action", "drop");
    set_field("vendor_event_outcome", "failed");
    set_fields(grok("no protocol handler for connection from %{IPORHOST:remote_ip}:%{NUMBER:remote_port}", to_string($message.message), true));
end

