rule "Veilid Parse Timestamp Ahead/Behind"
when
    starts_with(to_string($message.message), "Timestamp behind")
    || starts_with(to_string($message.message), "Timestamp ahead")
then
    // https://gitlab.com/veilid/veilid/-/blob/main/veilid-core/src/network_manager/mod.rs#L965
    set_field("vendor_event_category", "time");
    set_field("vendor_event_action", "check");
    set_fields(grok("Timestamp %{WORD:vendor_event_outcome}: %{NUMBER:millis_behind}ms \\(%{WORD:network_transport}:%{IPORHOST:destination_ip}:%{NUMBER:destination_port}\\)", to_string($message.message), true));
end

